actions.script:
var b2Vec2 = Box2D.Common.Math.b2Vec2;
var jumped = false;


// REPLACE ART
var AnimClass = Asset.resources["sprite.Fluttershy"];
var fluttershy = new AnimClass();
this.actor.art.removeChild(this.actor.art.children[0]);
this.actor.art.addChild(fluttershy);
var artPiece = this.actor.art.children[0];



this.update = function(){


var vel = this.actor.body.GetLinearVelocity();
var vx=vel.x, vy=vel.y;


artPiece.rotation = (artPiece.rotation+vy*5)/2;


if(vx>5) vx=5;
if(vx<-5) vx=-5;

if(Key.left){
		vx -= 1;
}

if(Key.right){
		vx += 1;
}

this.actor.body.SetLinearVelocity( new b2Vec2(vx,vy) );


var pos = this.actor.body.GetPosition();
var x = pos.x; var y = pos.y;
if(x<0){
x=8;
this.actor.body.SetPosition( new b2Vec2(x,y) );
}
if(x>8){
x=0;
this.actor.body.SetPosition( new b2Vec2(x,y) );
}


if(!jumped && Key.up){
jumped = true;
vy -= 5;
this.actor.body.SetLinearVelocity( new b2Vec2(vx,vy) );
}
if(jumped && !Key.up){
jumped= false;
}

var vel = this.actor.body.GetLinearVelocity();
var vx=vel.x, vy=vel.y;
if(vx<-0.1){
this.actor.art.scaleX = -1;
}
if(vx>0.1){
this.actor.art.scaleX = 1;
}



}.bind(this);

var b2Vec2 = Box2D.Common.Math.b2Vec2;
var jumped = false;
var jumpDelay = 0;

// REPLACE ART
var AnimClass = Asset.resources["sprite.Dashy"];
var fluttershy = new AnimClass();
this.actor.art.removeChild(this.actor.art.children[0]);
this.actor.art.addChild(fluttershy);
var artPiece = this.actor.art.children[0];


this.update = function(){

var player = Global.player;
if(!player) return;

var vel = this.actor.body.GetLinearVelocity();
var vx=vel.x, vy=vel.y;



artPiece.rotation = (artPiece.rotation+vy*5)/2;


var pos = this.actor.body.GetPosition();
var x = pos.x; var y = pos.y;

var pos2 = player.body.GetPosition();


if(vx>5) vx=5;
if(vx<-5) vx=-5;

if(pos2.x<x-1){
		vx -= 1;
this.actor.art.scaleX = -1;
}

if(pos2.x>x+1){
		vx += 1;
this.actor.art.scaleX = 1;
}

this.actor.body.SetLinearVelocity( new b2Vec2(vx,vy) );


if(x<0){
x=8;
this.actor.body.SetPosition( new b2Vec2(x,y) );
}
if(x>8){
x=0;
this.actor.body.SetPosition( new b2Vec2(x,y) );
}

if(!jumped && ( pos2.y<y || y>3.2 ) ){
jumped = true;
jumpDelay = 10;
vy -= 5;
this.actor.body.SetLinearVelocity( new b2Vec2(vx,vy) );
}
if(jumped && jumpDelay>0){
jumpDelay--;
if(jumpDelay<=0){
jumped= false;
}
}

var dx = pos2.x-x;
var dy = pos2.y-y;
var dist = dx*dx+dy*dy;
if(dist<0.6){
vx -= dx*4;
vy -= dy*4;
this.actor.body.SetLinearVelocity( new b2Vec2(vx,vy) );

var vel = player.body.GetLinearVelocity();
var vx=vel.x, vy=vel.y;
vx += dx*4;
vy += dy*4;
player.body.SetLinearVelocity( new b2Vec2(vx,vy) );

}

}.bind(this);

var b2Vec2 = Box2D.Common.Math.b2Vec2;

var state = "playing";
var splatTimeLeft = 0;
var _winner = "";

var timeToReset = false;

var flutterScore = 0;
var dashyScore = 0;

var doSplat = function(winner){
_winner = winner;
state = "splat";
splatTimeLeft = 30;

var player = Global.player;
var enemy = Global.enemy;

player.body.SetActive(false);
enemy.body.SetActive(false);

if(_winner=="fluttershy"){
flutterScore++;
}else{
dashyScore++;
}

};
this.update = function(){

var player = Global.player;
var enemy = Global.enemy;

if(!enemy || !player) return;

switch(state){
case "playing":

var pos1 = player.body.GetPosition();
var pos2 = enemy.body.GetPosition();

if(pos2.y>4){
doSplat("fluttershy");
}else if(pos1.y>4){
doSplat("rainbow dash");
}

break;




case "splat":
splatTimeLeft--;
this.actor.art.rotation = 10/(30-splatTimeLeft);
if(splatTimeLeft%2==0) this.actor.art.rotation*=-1;
if(splatTimeLeft==0){


if(flutterScore==10){
Playtime.showMessage("FLUTTERSHY WINS!");
timeToReset = true;
}else if(dashyScore==10){
Playtime.showMessage("RAINBOW DASH WINS!");
timeToReset = true;
}else{
Playtime.showMessage("Score: "+flutterScore+"-"+dashyScore);
}

state = "afterMessage";


}
break;


case "afterMessage":
player.body.SetActive(true);
enemy.body.SetActive(true);
player.body.SetPosition( new b2Vec2(0.6,3.2) );
enemy.body.SetPosition( new b2Vec2(7.3,3.2) );
player.body.SetLinearVelocity( new b2Vec2(0,0) );
enemy.body.SetLinearVelocity( new b2Vec2(0,0) );
state = "playing";

if(timeToReset){
flutterScore = 0; dashyScore = 0;
timeToReset = false;
}

break;

}

}.bind(this);
